@prefix geojson: <https://purl.org/geojson/vocab#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix topo: <https://www.ogc.org/rules/topo/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode _:N5f0ed2b5b11c4735a39ee505191f6cf9 ;
            sh:resultMessage "Lines topologies must reference points within current graph closure." ;
            sh:resultPath geojson:topology ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraint [ sh:message "Lines topologies must reference points within current graph closure." ;
                    sh:select """
        prefix rdf:     <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
        PREFIX rdfs:    <http://www.w3.org/2000/01/rdf-schema#>
        prefix geojson: <https://purl.org/geojson/vocab#>

        SELECT $this (geojson:topology as ?path) (?ref as ?value)
        WHERE {
         $this geojson:relatedFeatures/rdf:rest*/rdf:first ?ref .
         FILTER NOT EXISTS {
             ?ref geojson:geometry/rdf:type geojson:Point }
        }
""" ] ;
            sh:sourceConstraintComponent sh:SPARQLConstraintComponent ;
            sh:sourceShape topo:testTopoLinesReferencePoints ;
            sh:value <file:///github/workspace/P1> ],
        [ a sh:ValidationResult ;
            sh:focusNode _:N5f0ed2b5b11c4735a39ee505191f6cf9 ;
            sh:resultMessage "Referenced features must be present within current graph closure for  topological feature." ;
            sh:resultPath geojson:relatedFeatures ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraint [ sh:message "Referenced features must be present within current graph closure for  topological feature." ;
                    sh:select """
        prefix rdf:     <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
        PREFIX rdfs:    <http://www.w3.org/2000/01/rdf-schema#>
        prefix geojson: <https://purl.org/geojson/vocab#>

        SELECT $this (geojson:relatedFeatures as ?path) (?ref as ?value)
        WHERE {
         $this geojson:relatedFeatures/rdf:rest*/rdf:first ?ref .
         $this a ?geomtype .
         FILTER ( ?geomtype != geojson:Polygon)
         FILTER NOT EXISTS { ?ref ?p ?o }
        }
""" ] ;
            sh:sourceConstraintComponent sh:SPARQLConstraintComponent ;
            sh:sourceShape topo:testTopoReferencesExist ;
            sh:value <file:///github/workspace/P1> ] .

_:Nfa1dccb5eea9434f9fed4ee84754e255 rdf:first <file:///github/workspace/P1> ;
